mapper.insert:
/* FileMapper - insertAttachFile */
INSERT
INTO ATTACH_FILE
( ID
, NAME
, PATH
, CAPACITY
, EXTENSION
, CREATED_DATE_TIME
, CREATOR )
VALUES
( #{file.id}
, #{file.name}
, #{file.path}
, #{file.capacity}
, #{file.extension}
, SYSTIMESTAMP
, #{session.userId} )

mapper.delete:
/* FileMapper - deleteAttachFile */
DELETE
FROM ATTACH_FILE
WHERE ID = #{fileId}

/* FileMapper - deleteAttachFileGroupByFileId */
DELETE
FROM ATTACH_FILE_GROUP
WHERE ATTACH_FILE_ID = #{fileId}

mapper.insert:
INSERT
  INTO ATTACH_FILE_GROUP
       (
         ID
       , ENTITY_ID
       , ATTACH_FILE_ID
       , CREATED_DATE_TIME
       , CREATOR
       )
VALUES (
         #{fileGroup.id}
       , #{fileGroup.entityId}
       , #{fileGroup.attachFileId}
       , SYSTIMESTAMP
       , #{session.userId}
       )

mapper.delete:
/* FileMapper - deleteAttachFileGroupByEntityId */
DELETE
FROM ATTACH_FILE_GROUP
WHERE ENTITY_ID = #{entityId}
